listeners.selectionchanged:
function() {
var jsonObj = this.comboBox.store.reader.arrayData;
for(var index = 0; index < jsonObj.length;index++) {
if (this.getValue() == jsonObj[index][0]){
break;
}
if (index + 1 == jsonObj.length){
this.comboBox.setValue('');
CQ.Ext.Msg.show({title:CQ.I18n.getMessage('Validation Failed'),
msg: CQ.I18n.getMessage('Please Enter Correct Topic Value in the DropDown'),
buttons: CQ.Ext.Msg.OK,icon: CQ.Ext.Msg.ERROR});
}
}
}

listeners.click:
function() {

                                    var inputValueTopic = $('[name="./newTopicName"]').val();

                                    if( inputValueTopic == '' ) {
                                        alert('Please add a value Topic to create');
                                        return false;
                                    }

                                    var getUrl = window.location.href,
                                        urlCut = '/content' + getUrl.split('/content').slice(1),
                                        urlLanguage = urlCut.split('/').slice(1,5).join('/'),
                                        slashUrl = '/' + urlLanguage,
                                        urlResponse = 'http:/' + getUrl.split('/').slice(1,3).join('/');

                                        if ($('#successTopic')) { $('#successTopic').remove(); }
                                        if ($('#errorTopic')) { $('#errorTopic').remove(); }

									$.ajax({
										url : '/bin/createNewPageServlet',
										data : {
											title : inputValueTopic,
											parentPath : slashUrl,
											template : 'topic'
										},
										success : function(responseLink) {
                                            $('.x-viewport #cq-sk-tab-PAGE table:first').trigger('click');
                                            $(document).ready(function() {
											    responseLink = responseLink.trim();
											    $('button:contains("Create New Topic")').closest('div').append('<div id="successTopic" style="position:relative;left:115px;top:-18px;width:200px" >New Topic successfully created </br> <a style="text-decoration:underline;color:#15428B;" target="_blank" href=' + urlResponse + responseLink + '> View Topic</a> </div>');
                                            });
										},
										error : function() {
											$('button:contains("Create New Topic")').closest('div').append('<div id="errorTopic" style="position:relative;color:red;left:115px;top:-18px;width:200px">Unable to create New Topic</div>');
										}
									}
								);}

