listeners.loadcontent:
function(box,value){
var dia = box.findParentByType('dialog'); 
var f = this.findParentByType('form').getForm();
var notresponsive=dia.find('name','./notResponsive');
var selectionField = f.findField('./contentType'); 
var selValue = selectionField.getValue();
if(selValue == 'iframe'){ 
notresponsive[0].show();
} 
else{ 
notresponsive[0].hide();
} 
}

