function() 
{ 
var selectFieldName = this.name.replace('linkType', 'newWindow'); 
var fld = this.findParentByType('form').find('name', selectFieldName)[0];  
var value = this.getValue();
if(value == 'internal' )
{ 
fld.el.dom.checked=false;
}
else
{ 
fld.el.dom.checked=true;
}
}

listeners.dialogselect:
function() {
var temp = this.name;
temp = temp+'$'
var longTitle = temp.replace('link$', 'longTitle'); 
var shortTitle = temp.replace('link$', 'shortTitle'); 
var fieldLongTitle = this.findParentByType('form').find('name', longTitle)[0]; 
var fieldShortTitle = this.findParentByType('form').find('name', shortTitle)[0]; 
var titlelink  = this.getValue(); $.ajaxSetup({ async: false }); 
$.getJSON("/apps/journey/components/common/dialogConfig/titleslist/data.json",
{     
link: titlelink,     
},
function(data) { 
var longTitle = data.longTitle; 
var shortTitle = data.shortTitle; 
if(longTitle) { 
fieldLongTitle.setValue(longTitle); 
} 
if(shortTitle) { 
fieldShortTitle.setValue(shortTitle); 
} 
}); 
};

